import React from 'react';

const Header = () => {
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleNavigation = (sectionId) => {
    scrollToSection(sectionId);
  };

  return (
    <header>
      <h1>Звукова Студія</h1>
      <nav>
        <ul>
          <li><a href="#intro" onClick={() => handleNavigation('intro')}>Головна</a></li>
          <li><a href="#about" onClick={() => handleNavigation('about')}>Про нас</a></li>
          <li><a href="#services" onClick={() => handleNavigation('services')}>Наші Послуги</a></li>
          <li><a href="#portfolio" onClick={() => handleNavigation('portfolio')}>Портфоліо</a></li>
          <li><a href="#director" onClick={() => handleNavigation('director')}>Від Директора</a></li>
          <li><a href="#more-serv" onClick={() => handleNavigation('more-serv')}>Більше Послуг</a></li>
          <li><a href="#clients" onClick={() => handleNavigation('clients')}>Відгуки</a></li>
          <li><a href="/bonuspage">Adobe Audition</a></li>
          <li><a href="/extrapage">FL Studio</a></li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
