import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import Header from './components/Header';
import Footer from './components/Footer';
import Intro from './components/sections/Intro';
import About from './components/sections/About';
import Services from './components/sections/Services';
import Portfolio from './components/sections/Portfolio';
import Director from './components/sections/Director';
import MoreServices from './components/sections/MoreServ';
import Testimonials from './components/sections/Clients';
import Contacts from './components/sections/Contacts';
import BonusPage from './components/pages/BonusPage';
import ExtraPage from './components/pages/ExtraPage';
import './styles/styles.css';
import './App.css';

function App() {
  const navigate = useNavigate(); // Додайте useNavigate

  return (
    <Router>
      <div className="App">
        <Header navigate={navigate} /> {/* Передайте navigate в компонент Header */}
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Intro />
                <About />
                <Services />
                <Portfolio />
                <Director />
                <MoreServices />
                <Testimonials />
                <Contacts />
                <Footer />
              </>
            }
          />
          <Route path="/bonuspage" element={<BonusPage />} />
          <Route path="/extrapage" element={<ExtraPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
