import React, { useState } from 'react';
import './components/styles/MiniPlayer.css'; // Додайте файл стилів MiniPlayer.css

const MiniPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(true);
  const [volume, setVolume] = useState(50);

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
    // Додайте код для відтворення або призупинення музики тут
  };

  const handleVolumeChange = (e) => {
    setVolume(e.target.value);
    // Додайте код для зміни гучності тут
  };

  return (
    <div className="mini-player">
      <button className={`play-pause ${isPlaying ? 'playing' : ''}`} onClick={togglePlayPause}>
        {isPlaying ? 'Pause' : 'Play'}
      </button>
      <input
        type="range"
        min="0"
        max="100"
        value={volume}
        onChange={handleVolumeChange}
        className="volume-slider"
      />
      <button className="stop-button">
        Stop
      </button>
    </div>
  );
};

export default MiniPlayer;
