import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const navigate = useNavigate();

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleNavigation = (to, sectionId) => {
    if (to === '/bonuspage' || to === '/extrapage') {
      navigate(to);
    } else {
      scrollToSection(sectionId);
    }
  };

  return (
    <header>
      <h1>Звукова Студія</h1>
      <nav>
        <ul>
          <a onClick={() => handleNavigation('/', 'intro')}>Головна</a>
          <a onClick={() => handleNavigation('/', 'about')}>Про нас</a>
          <a onClick={() => handleNavigation('/', 'services')}>Наші Послуги</a>
          <a onClick={() => handleNavigation('/', 'portfolio')}>Портфоліо</a>
          <a onClick={() => handleNavigation('/', 'director')}>Від Директора</a>
          <a onClick={() => handleNavigation('/', 'more-serv')}>Більше Послуг</a>
          <a onClick={() => handleNavigation('/', 'clients')}>Відгуки</a>
          <a onClick={() => navigate('/bonuspage')}>Adobe Audition</a>
          <a onClick={() => navigate('/extrapage')}>FL Studio</a>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
