import React, { useState } from 'react';
import 'src/styles/ColorPalette.css';

const ColorItem = ({ color }) => {
    const [isCopied, setIsCopied] = useState(false);
  
    const handleClick = () => {
      navigator.clipboard.writeText(color.color);
  
      const audio = new Audio('/path/to/your/sound.mp3');
      audio.play();
  
      setIsCopied(true);
  
      setTimeout(() => {
        setIsCopied(false);
      }, 2000);
    };
  
    return (
      <div className="color-item" style={{ backgroundColor: color.color }} onClick={handleClick}>
        {isCopied && <span className="copy-text">Copied!</span>}
      </div>
    );
  };

export default ColorItem;
