import React, { useState } from 'react';
import srcNotify from '../src_notify.mp3';

const ColorItem = ({ color }) => {
    const [isCopied, setIsCopied] = useState(false);
  
    const handleCopy = () => {
      navigator.clipboard.writeText(color);
  
      const audio = new Audio(srcNotify);
      audio.play();
  
      setIsCopied(true);
  
      setTimeout(() => {
        setIsCopied(false);
      }, 2000);
    };
  
    const handleColorClick = () => {
      document.body.style.backgroundColor = color;
  
      const audio = new Audio(srcNotify);
      audio.play();
  
      navigator.clipboard.writeText(color);
    };
  
    return (
      <div
        className={`color-item ${isCopied ? 'copied' : ''}`}
        style={{ backgroundColor: color }}
        onClick={handleCopy}
      >
        <span className="copy-me">Copy me</span>
        <span onClick={handleColorClick}>{color}</span>
      </div>
    );
  };

export default ColorItem;
