import React from 'react';
import './ColorPalette.css';

const ColorItem = ({ color }) => {
    const [isCopied, setIsCopied] = useState(false);
  
    const handleClick = () => {
      // Копирование цвета в буфер обмена
      navigator.clipboard.writeText(color.color);
  
      // Воспроизведение короткого звука (при необходимости)
      const audio = new Audio('/path/to/your/sound.mp3');
      audio.play();
  
      // Устанавливаем состояние для отображения сообщения об успешном копировании
      setIsCopied(true);
  
      // Сброс состояния через 2 секунды
      setTimeout(() => {
        setIsCopied(false);
      }, 2000);
    };
  
    return (
      <div className="color-item" style={{ backgroundColor: color.color }} onClick={handleClick}>
        {isCopied && <span className="copy-text">Copied!</span>}
      </div>
    );
  };

export default ColorItem;
